mapper.sql:
where cid=#{cond.cid}

	and trade.status = #{cond.status}


	and trade.trade_num = #{cond.tradeNum}


	and trade.trade_num like concat(#{cond.tradeNumMatchBefore} , '%')

mapper.select:
select 

from trade



	order by ${pager.sort}

		${pager.dir}



	limit #{pager.start}, #{pager.limit}
